mapper.insert:

    select next value for seq_hea_drug

insert into hea_drug
( 
    hea_drug_no
    ,hea_drug_nm
    ,unit
    ,amount_limit
    ,amount_curr
    ,sort_order
    ,use_yn
    ,create_user_id
    ,create_dt 
)
VALUES
( 
    #{heaDrugNo}
    ,#{heaDrugNm}
    ,#{unit}
    ,#{amountLimit}
    ,#{amountCurr}
    ,#{sortOrder}
    ,#{useYn}
    ,#{createUserId}
    ,getDate()
)

mapper.update:
update hea_drug
SET hea_drug_nm = #{heaDrugNm}
    ,unit = #{unit}
    ,amount_limit = #{amountLimit}
    ,amount_curr = #{amountCurr}
    ,sort_order = #{sortOrder}
    ,use_yn = #{useYn}
    ,update_user_id = #{updateUserId}
    ,update_dt = getDate()
where hea_drug_no = #{heaDrugNo}

update hea_drug 
set amount_curr=(amount_curr-#{amount})
where hea_drug_no=#{heaDrugNo}

update hea_drug 
set amount_curr=amount_curr+#{amount}
where hea_drug_no=#{heaDrugNo}

mapper.select:
select sum(amount.value)
from(
select 
    amount as value
from hea_prescribe 
where 1=1
and hea_drug_no=#{heaDrugNo}
and hea_infirmary_usage_no = #{heaInfirmaryUsageNo}
union
select 0 as value
)amount

mapper.delete:
delete from hea_prescribe
where hea_infirmary_usage_no=#{heaInfirmaryUsageNo}
and hea_drug_no=#{heaDrugNo}
