mapper.select:
/* AuthGrpMapper.countAuthGrp [AuthGrp.xml] */
SELECT count(*)
  FROM com_auth_grp
 WHERE auth_grp_nm = #{authGrpNm}

mapper.insert:
/* AuthGrpMapper.createAuthGrp [AuthGrp.xml] */

    SELECT next value for seq_com_auth_grp

INSERT INTO com_auth_grp
      (auth_grp_no
      ,auth_grp_nm
      ,remarks
      ,use_yn
      ,create_user_id
      ,create_dt)
VALUES
      (#{authGrpNo}
      ,#{authGrpNm}
      ,#{remarks}
      ,#{useYn}
      ,#{createUserId}
      ,getdate())

mapper.update:
/* AuthGrpMapper.updateAuthGrp [AuthGrp.xml] */
UPDATE com_auth_grp
   SET auth_grp_nm = #{authGrpNm}
      ,remarks = #{remarks}
      ,use_yn = #{useYn}
      ,update_user_id = #{updateUserId}
      ,update_dt = getdate()
 WHERE auth_grp_no = #{authGrpNo}

mapper.select:
/* AuthGrpMapper.getAuthGrpUsers [AuthGrp.xml] */ SELECT plant.code as plant_cd ,plant.code_nm as plant_nm ,cu.dept_cd ,cd.dept_nm ,cu.user_id ,cu.user_nm FROM com_user cu INNER JOIN com_dept cd ON cu.dept_cd = cd.dept_cd INNER JOIN com_code_master plant ON plant.code_group_cd = 'COM_PLANT_CD' AND cu.plant_cd = plant.code WHERE 1=1
AND cu.plant_cd = #{plantCd}
AND cd.dept_cd = #{deptCd}
AND (cu.user_nm like '%' + #{keyword} + '%' OR exists(SELECT cag.auth_grp_nm FROM com_auth_grp cag INNER JOIN com_user_auth_grp cuag ON cag.auth_grp_no = cuag.auth_grp_no WHERE cuag.user_id = cu.user_id AND cag.auth_grp_nm like '%' + #{keyword} + '%'))

mapper.delete:
/* AuthGrpMapper.deleteAuthGrpUser [AuthGrp.xml] */
DELETE FROM com_user_auth_grp
 WHERE auth_grp_no = #{authGrpNo}
   AND user_id = #{userId}

/* AuthGrpMapper."deleteAuthGrpDept" [AuthGrp.xml] */
DELETE FROM com_dept_auth_grp
 WHERE auth_grp_no = #{authGrpNo}
   AND dept_cd = #{deptCd}
