mapper.select:
/* UserAuthGrpMapper.getUserAuthGrps [UserAuthGrp.xml] */ select agp.auth_grp_no ,agp.auth_grp_nm ,ugp.user_id ,ugp.user_nm ,case when ugp.user_id is null then 'N' else 'Y' end as setting_yn from com_auth_grp agp left outer join ( select ugp.auth_grp_no ,usr.user_id ,usr.user_nm from com_user_auth_grp ugp inner join com_user usr on ugp.user_id = usr.user_id
and usr.user_id = #{userId}
and usr.dept_cd = #{deptCd}
union select ugp.auth_grp_no ,#{userId} as user_id ,(select user_nm from com_user where user_id = #{userId}) as user_nm from com_dept_auth_grp ugp inner join com_dept usr on ugp.dept_cd = usr.dept_cd where ugp.dept_cd = (select dept_cd from com_user where user_id = #{userId})
and usr.dept_cd = #{deptCd}
) ugp on agp.auth_grp_no = ugp.auth_grp_no where 1 = 1 and agp.use_yn = 'Y' order by agp.auth_grp_nm

mapper.delete:
/* UserAuthGrpMapper.deleteUserAuthGrp [UserAuthGrp.xml] */
    delete
    from com_user_auth_grp
    where 1 = 1
    and auth_grp_no = #{authGrpNo}
    and user_id = #{userId}

/* UserAuthGrpMapper.deleteDeptAuthGrp [UserAuthGrp.xml] */
    delete
    from com_dept_auth_grp
    where 1 = 1
    and auth_grp_no = #{authGrpNo}
    and dept_cd = #{deptCd}

mapper.insert:
/* UserAuthGrpMapper.saveDeptAuth [UserAuthGrp.xml] */
    merge into com_dept_auth_grp as a
    using (select 'X' as DUAL) b
    on (a.dept_cd = #{deptCd}
        and a.auth_grp_no = #{authGrpNo}
    )
    when not matched then
    insert
        (dept_cd
        ,auth_grp_no
        ,plant_access_yn
        ,dept_access_yn
        ,create_user_id
        ,create_dt)
    values
        (#{deptCd}
        ,#{authGrpNo}
        ,isnull(#{plantAccessYn}, 'N')
        ,isnull(#{deptAccessYn}, 'N')
        ,#{createUserId}
        ,getdate())
     when matched then


            update set  plant_access_yn = #{plantAccessYn}


            update set  dept_access_yn = #{deptAccessYn}
                        ,plant_access_yn = #{plantAccessYn}


            update set  plant_access_yn = #{plantAccessYn}
                       , dept_access_yn = #{deptAccessYn}


     ;

/* UserAuthGrpMapper.saveUserAuth [UserAuthGrp.xml] */
    merge into com_user_auth_grp as a
    using (select 'X' as DUAL) b
    on (a.user_id = #{userId}
        and a.auth_grp_no = #{authGrpNo}
    )
    when not matched then
    insert
        (user_id
        ,auth_grp_no
        ,plant_access_yn
        ,dept_access_yn
        ,create_user_id
        ,create_dt)
    values
        (#{userId}
        ,#{authGrpNo}
        ,isnull(#{plantAccessYn}, 'N')
        ,isnull(#{deptAccessYn}, 'N')
        ,#{createUserId}
        ,getdate())
     when matched then


            update set  plant_access_yn = #{plantAccessYn}


            update set  dept_access_yn = #{deptAccessYn}
                        ,plant_access_yn = #{plantAccessYn}


            update set  plant_access_yn = #{plantAccessYn}
                       , dept_access_yn = #{deptAccessYn}


     ;

mapper.select:
/* UserAuthGrpMapper.getAuthGrpSubDepts [UserAuthGrp.xml] */ with depttree as ( select cd.dept_cd ,cd.dept_nm ,cd.p_dept_cd from com_dept cd where 1=1 and cd.dept_cd in
#{deptCd}
and cd.use_yn = 'Y' union all select cdd.dept_cd ,cdd.dept_nm ,cdd.p_dept_cd from com_dept cdd inner join depttree on cdd.p_dept_cd = depttree.dept_cd where 1=1 and cdd.use_yn = 'Y' ) select distinct depttree.dept_cd ,depttree.dept_nm from depttree
