mapper.select:
/* MgtBudgetingItemMapper.getBudgetActList [MgtBudgetingItemMapper.xml] */ SELECT mba.budget_act_no , mbad.budget_act_dept_no , mbadc.budget_act_dept_cate_no , mba.year , mbad.dept_cd , dbo.GET_DEPT_NM(mbad.dept_cd) as dept_nm , mbadc.budget_cate_cd , mbadc.budget_cate_nm , mbadc.budget_cate_dtl_cd , mbadc.budget_cate_dtl_nm , mbadc.org_amt , mbadc.remark , mbadc.create_user_id , convert(char(10), mbadc.create_dt, 23) as create_dt from mgt_budget_act mba inner join mgt_budget_act_dept mbad on mba.budget_act_no = mbad.budget_act_no LEFT OUTER JOIN mgt_budget_act_dept_cate mbadc on mbad.budget_act_dept_no = mbadc.budget_act_dept_no where 1 = 1 and mbadc.budget_act_dept_cate_no is not null
and mba.year = #{year}
and mbad.dept_cd = #{deptCd}
and mbadc.budget_cate_cd = #{budgetCateCd}
and mbadc.budget_cate_dtl_nm like '%' + #{budgetCateDtlNm} + '%'
order by mba.year

/* MgtBudgetingItemMapper.getBudgetingItemList [MgtBudgetingItemMapper.xml] */ SELECT mbc.budget_cate_cd , mbc.budget_cate_nm , mbcd.budget_cate_dtl_cd , mbcd.budget_cate_dtl_nm , mbcd.sort_order , mbcd.create_user_id , mbcd.remark from mgt_budget_category mbc inner join mgt_budget_category_dtl mbcd on mbc.budget_cate_cd = mbcd.budget_cate_cd where 1 = 1 and mbc.use_yn = 'Y' and mbcd.use_yn = 'Y'
and mbc.budget_cate_cd = #{budgetCateCd}
and mbcd.budget_cate_dtl_nm like '%' + #{budgetCateDtlNm} + '%'
order by mbc.create_dt desc, mbcd.sort_order

mapper.insert:
/* MgtBudgetingItemMapper.insertBudgetAct [MgtBudgetingItemMapper.xml] */
	INSERT INTO mgt_budget_act(
		budget_act_no
		,year
		,create_user_id
		,create_dt
	) values (
		#{budgetActNo}
		,#{year}
		,#{createUserId}
		,getdate()
	)

/* MgtBudgetingItemMapper.insertBudgetActDept [MgtBudgetingItemMapper.xml] */
	INSERT INTO mgt_budget_act_dept
	(
		budget_act_no
		, budget_act_dept_no
		, dept_cd
	) values (
		#{budgetActNo}
		,#{budgetActDeptNo}
		,#{deptCd}
	)

mapper.update:
	/* MgtBudgetingItemMapper.saveBudgetActDeptCate [MgtBudgetingItemMapper.xml] */

            select next value for seq_mgt_budget_act_dept_cate as budgetActDeptCateNo

	MERGE into mgt_budget_act_dept_cate d
	using (select #{budgetActDeptNo} as budget_act_dept_no
		,	#{budgetCateCd} as budget_cate_cd 
		,	#{budgetCateDtlCd} as budget_cate_dtl_cd 
	) s
	on (d.budget_act_dept_no = s.budget_act_dept_no and d.budget_cate_cd = s.budget_cate_cd and d.budget_cate_dtl_cd = s.budget_cate_dtl_cd)
	WHEN MATCHED THEN 
	 update set 
		d.org_amt = #{orgAmt}
		,d.update_user_id = #{updateUserId}
		,d.update_dt = getdate()
	WHEN NOT MATCHED THEN     
	INSERT ( 
       	budget_act_dept_no
		,budget_act_dept_cate_no
		,budget_cate_cd
		,budget_cate_nm
		,budget_cate_dtl_cd
		,budget_cate_dtl_nm
		,org_amt
		,remark
		,create_user_id
		,create_dt
		)
		values(
		 #{budgetActDeptNo}
		, #{budgetActDeptCateNo}
		, #{budgetCateCd}
		, #{budgetCateNm}
		, #{budgetCateDtlCd}
		, #{budgetCateDtlNm}
		, #{orgAmt}
		, #{remark}
		, #{createUserId}
		,getdate()
		);

mapper.delete:
/* MgtBudgetingItemMapper.deleteBudgetActDeptCate [MgtBudgetingItemMapper.xml] */
delete from mgt_budget_act_dept_cate
where budget_act_dept_no = #{budgetActDeptNo}
and budget_act_dept_cate_no = #{budgetActDeptCateNo}
