( 
    saf_train_plan_no        
    ,video_url
    ,video_total_time
    ,video_comp_time
    ,use_yn
    ,create_user_id
    ,create_dt

)
VALUES
( 
    #{safTrainPlanNo}
    ,#{videoUrl}
    ,#{videoTotalTime}
    ,#{videoCompTime}
    ,'Y'
    ,#{createUserId}
    ,getDate()
)

mapper.update:
/* EmergencyMapper.updateEmergencyVideo [Emergency.xml] */
update saf_train_plan_video
SET video_url = #{videoUrl}
    ,video_total_time= #{videoTotalTime}
    ,video_comp_time= #{videoCompTime}
    , update_user_id = #{updateUserId}
    , update_dt = getdate()
where saf_train_plan_no = #{safTrainPlanNo}

mapper.delete:
/* EmergencyMapper.deleteEmergencyVideo [Emergency.xml] */
delete from saf_train_plan_video
where saf_train_plan_no = #{safTrainPlanNo}
