/* EmergencyMapper.getEmergencyLists [Emergency.xml] */ select saf_train_plan_no -- 훈련계획번호 ,tp.dept_cd --주관부서코드 ,dbo.GET_DEPT_NM(tp.dept_cd) as dept_nm ,tp.train_type_cd -- 훈련구분코드 SAF_TRA_COURSE_CLS ,dbo.GET_CODE_NM(tp.train_type_cd, 'SAF_TRA_COURSE_CLS') as train_type_nm ,tp.train_user_id -- 훈련교관ID ,tp.train_dept_cd -- 훈련교관소속부서코드 ,tp.train_nm -- 훈련명 ,tp.train_start_ymd -- 훈련시작일 ,tp.train_end_ymd -- 훈련종료일 ,tp.train_s_time -- 훈련시작시간 ,tp.train_e_time -- 훈련종료시간 ,tp.train_s_hour -- 훈련시작시 ,tp.train_s_min -- 훈련시작분 ,tp.train_e_hour -- 훈련종료시 ,tp.train_e_min -- 훈련종료분 ,tp.train_place -- 훈련장소 ,tp.train_content -- 훈련내용요약 ,tp.train_s_time + ' ~ ' + tp.train_e_time as trainTime ,tp.create_user_id -- 등록자 ,dbo.GET_USER_NM(tp.create_user_id) as create_user_nm ,convert(char(10), tp.create_dt, 23) as create_dt -- 등록일 ,tp.update_user_id -- 수정자 ,tp.update_dt -- 수정일 ,( select count(*) from saf_train_dept_rslt tds where tds.saf_train_plan_no = tp.saf_train_plan_no ) as subDeptCnt ,format(( select count(*) from saf_train_dtl_psn where saf_train_plan_no = tp.saf_train_plan_no ), '##,##0') as userCnt ,( select count(*) from saf_train_dtl_psn where saf_train_plan_no = tp.saf_train_plan_no and complet_yn = 'N' ) as stateNCnt ,( select count(*) from saf_train_dtl_psn where saf_train_plan_no = tp.saf_train_plan_no and complet_yn = 'N' and datediff(minute, GETDATE() , tp.train_end_ymd + ' '+ tp.train_e_time) < 0 ) as overDateCnt ,(select count(*) from saf_train_dtl_psn where saf_train_plan_no = tp.saf_train_plan_no and complet_yn = 'Y' ) as stateYCnt ,STUFF(( select ', ' + dbo.GET_CODE_NM_LANG(#{defaultParam.lang}, 'COM_PLANT_CD', tpp.plant_cd) as plant_nm from saf_train_plan_plant tpp where tp.saf_train_plan_no = tpp.saf_train_plan_no FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 2, '') as plant_nm ,tp.train_plan_state ,dbo.GET_CODE_NM_LANG(#{defaultParam.lang}, 'TRAIN_PLAN_STATE', tp.train_plan_state) as train_plan_state_nm from saf_train_plan tp where 1=1
and ( select count(*) from saf_train_plan_plant tpp where tpp.saf_train_plan_no = tp.saf_train_plan_no and tpp.plant_cd = #{plantCd} ) != 0
and tp.train_type_cd = #{trainTypeCd}
and tp.train_nm like '%' + #{trainNm} + '%'
and tp.train_place like '%' + #{trainPlace} + '%'
and tp.train_plan_state = #{trainPlanState}
and ( tp.train_plan_state = '${@com.she.utils.ConstVal@TRAIN_PLAN_STATE_ING}' OR tp.train_plan_state = '${@com.she.utils.ConstVal@TRAIN_PLAN_STATE_PLAN}')
and tp.re_train_yn = #{reTrainYn}
and tp.train_method_cd = #{trainMethodCd}
and (convert(char(10), train_start_ymd, 23) <= #{endDt} and convert(char(10), train_end_ymd, 23) >= #{startDt} )
and tp.dept_cd in (select dept_cd from dbo.GET_UNDER_DEPTS(#{deptCd}))
and tp.dept_cd = #{deptCd}
order by tp.saf_train_plan_no desc

/* EmergencyMapper.getEmergencyUserList [Emergency.xml] */ select cu.user_id ,cu.user_nm ,tdp.dept_cd ,dbo.GET_DEPT_NM(tdp.dept_cd) as dept_nm ,cu.plant_cd ,dbo.GET_CODE_NM_LANG(#{defaultParam.lang}, 'COM_PLANT_CD', cu.plant_cd) as plant_nm ,cu.position_nm ,tdp.remark ,tdp.complet_yn ,tdp.re_train_not_yn ,tdp.complet_ymd ,tdp.video_watch_time ,tdp.re_complet_yn from com_user cu inner join saf_train_dtl_psn tdp on cu.user_id = tdp.user_id inner join saf_train_plan tp on tp.saf_train_plan_no = tdp.saf_train_plan_no where tdp.saf_train_plan_no = #{safTrainPlanNo}
