   set dept_cd = #{deptCd}
      ,train_type_cd = #{trainTypeCd}
      ,train_user_id = #{trainUserId}
      ,train_dept_cd = #{trainDeptCd}
      ,train_nm = #{trainNm}
      ,train_start_ymd = #{trainStartYmd}
      ,train_end_ymd = #{trainEndYmd}
