update saf_train_plan 
set train_plan_state = '${@com.she.utils.ConstVal@TRAIN_PLAN_STATE_RESULT}'
where saf_train_plan_no = #{safTrainPlanNo}

mapper.select:
with plant as ( select code as plantCd from com_code_master where code_group_cd = 'COM_PLANT_CD' and code != '${@com.she.utils.ConstVal@PLANT_CD_00}' and code != '${@com.she.utils.ConstVal@PLANT_CD_10}' ), gubunList as ( select p.plantCd ,g.gubun_type ,g.gubun ,g.sort_order from plant p inner join ( select 'assess' as gubun_type ,'계획(조직수)' as gubun ,1 as sort_order union select 'assess' as gubun_type ,'실시(조직수)' as gubun ,2 as sort_order union select 'assess' as gubun_type ,'훈련 실시율(%)' as gubun ,3 as sort_order union select 'impr' as gubun_type ,'요청(건수)' as gubun ,4 as sort_order union select 'impr' as gubun_type ,'조치미완료(건수)' as gubun ,5 as sort_order union select 'impr' as gubun_type ,'기한초과(건수)' as gubun ,6 as sort_order union select 'impr' as gubun_type ,'조치완료(건수)' as gubun ,7 as sort_order union select 'impr' as gubun_type ,'개선 조치율(%)' as gubun ,8 as sort_order ) g on 1=1 ), riskList as ( select tpp.plant_cd as plantCd ,dbo.GET_CODE_NM(tpp.plant_cd, 'COM_PLANT_CD') as plant_nm ,iif(tp.train_plan_state = 'TPS03', '실시(조직수)', '계획(조직수)') AS gubun ,convert(numeric(10),sum(case when left(tp.train_end_ymd, 4) < #{year} then 1 else 0 end)) as oldCnt ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 1 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon1 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 2 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon2 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 3 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon3 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 4 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon4 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 5 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon5 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 6 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon6 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 7 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon7 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 8 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon8 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 9 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon9 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 10 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon10 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 11 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon11 ,convert(numeric(10),sum(case when substring(convert(char(10), tp.train_end_ymd, 23), 6, 2) = 12 and left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as mon12 ,convert(numeric(10),sum(case when left(tp.train_end_ymd, 4) = #{year} then 1 else 0 end)) as total ,convert(numeric(10),sum(case when left(tp.train_end_ymd, 4) <= #{year} then 1 else 0 end)) as allCnt from saf_train_plan tp inner join saf_train_plan_plant tpp on tp.saf_train_plan_no = tpp.saf_train_plan_no where 1=1 and tp.train_plan_state in ('TPS02','TPS03')
