mapper.select:
/* ElectHisMapper.getElectHises [ElectHis.xml] */ select * ,case when dbo.GET_USER_NM(n.update_user_id) = '' then dbo.GET_USER_NM(n.create_user_id) else dbo.GET_USER_NM(n.update_user_id) end as writer_user_nm ,case when n.update_dt is null then convert(date, n.create_dt) else convert(date, n.update_dt) end as writer_dt from ( select seh.saf_elect_his_no ,seh.saf_elect_his_grp_no
,ROW_NUMBER() OVER (PARTITION BY seh.saf_elect_his_grp_no ORDER BY seh.create_dt desc) as saf_no ,seh.plant_cd ,plant.code_nm as plant_nm ,seh.saf_elect_titl_no ,seh.dept_cd -- 부서 ,seh.user_id -- ,seh.ref_contents ,seh.elect_reason ,seh.elect_dt ,seh.elect_end_dt ,seh.lcn_type_nm ,seh.lcn_get_dt ,seh.lcn_num ,seh.use_yn ,seh.create_user_id ,seh.rev_num ,seh.rev_contents ,t.ref_law_cd ,law.code_nm as ref_law_nm ,t.elect_att_cd ,ts.code_nm as elect_att_nm ,t.elect_titl_nm ,t.qualifi_require ,cd.dept_nm ,cu.user_nm as user_nm ,createUser.user_nm as create_user_nm ,convert(char(10), seh.create_dt, 23) as create_dt ,seh.update_user_id ,updateUser.user_nm as update_user_nm ,convert(char(10), seh.update_dt, 23) as update_dt from saf_elect_his seh inner join saf_elect_titl t on seh.saf_elect_titl_no = t.saf_elect_titl_no inner join dbo.LANG_CODE_MASTER(#{defaultParam.lang}, 'COM_PLANT_CD') plant on seh.plant_cd = plant.code inner join com_user cu on seh.user_id = cu.user_id and cu.use_yn = 'Y' inner join com_dept cd on cu.dept_cd = cd.dept_cd inner join com_user createUser on seh.create_user_id = createUser.user_id and createUser.use_yn = 'Y' left outer join com_user updateUser on seh.update_user_id = updateUser.user_id and updateUser.use_yn = 'Y' inner join dbo.LANG_CODE_MASTER(#{defaultParam.lang}, 'TASK_GRP') ts on t.elect_att_cd = ts.code inner join dbo.LANG_CODE_MASTER(#{defaultParam.lang}, 'MGT_LAW_CLASS') law on t.ref_law_cd = law.code where 1=1 and seh.use_yn = 'Y'
and seh.plant_cd = #{plantCd}
and (seh.elect_end_dt is null or seh.elect_end_dt = '')
and cu.user_nm like '%' + #{userNm} + '%'
and seh.saf_elect_titl_no = #{safElectTitlNo}
and convert(datetime, seh.elect_dt) between convert(datetime, #{lcnGetStartDt}) and convert(datetime, #{lcnGetEndDt})
) n where 1=1 and saf_no = 1
order by plant_cd, elect_att_nm, elect_titl_nm, writer_dt desc

/* ElectHisMapper.getElectHis [ElectHis.xml] */ select seh.saf_elect_his_no ,seh.saf_elect_his_grp_no ,seh.plant_cd ,seh.saf_elect_titl_no ,seh.dept_cd -- 부서 ,seh.user_id -- ,seh.ref_contents ,seh.elect_reason ,seh.elect_dt ,seh.elect_end_dt ,seh.lcn_type_nm ,seh.lcn_get_dt ,seh.lcn_num ,seh.use_yn ,seh.create_user_id ,seh.create_dt ,seh.update_user_id ,seh.update_dt ,seh.rev_num ,seh.rev_contents ,t.ref_law_cd ,law.code_nm as ref_law_nm
,t.elect_att_cd ,ts.code_nm as elect_att_nm
,t.elect_titl_nm ,t.qualifi_require
,cd.dept_nm ,cu.user_nm as user_nm ,createUser.user_nm as create_user_nm ,convert(char(10), seh.create_dt, 23) as create_dt ,seh.update_user_id ,updateUser.user_nm as update_user_nm ,convert(char(10), seh.update_dt, 23) as update_dt from saf_elect_his seh inner join saf_elect_titl t on seh.saf_elect_titl_no = t.saf_elect_titl_no inner join com_user cu on seh.user_id = cu.user_id and cu.use_yn = 'Y' inner join com_dept cd on cu.dept_cd = cd.dept_cd inner join com_user createUser on seh.create_user_id = createUser.user_id and createUser.use_yn = 'Y' left outer join com_user updateUser on seh.update_user_id = updateUser.user_id and updateUser.use_yn = 'Y' inner join dbo.LANG_CODE_MASTER(#{defaultParam.lang}, 'TASK_GRP') ts on t.elect_att_cd = ts.code inner join dbo.LANG_CODE_MASTER(#{defaultParam.lang}, 'MGT_LAW_CLASS') law on t.ref_law_cd = law.code where seh.saf_elect_his_no = #{safElectHisNo}

mapper.insert:
/* ElectHisMapper.createElectHis [ElectHis.xml] */

        select next value for seq_saf_elect_his as saf_elect_his_no

        insert into saf_elect_his
            (
              saf_elect_his_no
             ,saf_elect_his_grp_no
             ,plant_cd
             ,saf_elect_titl_no
             ,dept_cd
             ,user_id
             ,ref_contents
             ,elect_reason
             ,elect_dt
             ,elect_end_dt
             ,lcn_type_nm
             ,lcn_get_dt
             ,lcn_num
             ,rev_num
             ,rev_contents
             ,use_yn
             ,create_user_id
             ,create_dt
            )
        values 
            (
              #{safElectHisNo}


                ,#{safElectHisNo}


                ,#{safElectHisGrpNo}


             ,#{plantCd}
             ,#{safElectTitlNo}
             ,#{deptCd}
             ,#{userId}
             ,#{refContents}
             ,#{electReason}
             ,#{electDt}
             ,#{electEndDt}
             ,#{lcnTypeNm}
             ,#{lcnGetDt}
             ,#{lcnNum}
             ,#{revNum}
             ,#{revContents}
             ,'Y'
             ,#{createUserId}
             ,getDate()
            )

mapper.update:
/* ElectHisMapper.updateOldElectHisUseYn [ElectHis.xml] */
    update saf_elect_his
       set use_yn = 'N'
     where saf_elect_his_grp_no = #{safElectHisGrpNo}

/* ElectHisMapper.updateElectHis [ElectHis.xml] */
    update saf_elect_his
    set 
         saf_elect_his_no         = #{safElectHisNo}
        ,saf_elect_his_grp_no    = #{safElectHisGrpNo}
        ,plant_cd                = #{plantCd}
        ,saf_elect_titl_no       = #{safElectTitlNo}
        ,dept_cd                 = #{deptCd}
        ,user_id                 = #{userId}
        ,ref_contents            = #{refContents}
        ,elect_reason            = #{electReason}
        ,elect_dt                = #{electDt}
        ,elect_end_dt            = #{electEndDt}
        ,lcn_type_nm             = #{lcnTypeNm}
        ,lcn_get_dt              = #{lcnGetDt}
        ,lcn_num                 = #{lcnNum}
        ,update_user_id          = #{updateUserId}
        ,update_dt               = getDate()
        ,rev_num                 = #{revNum}
    where saf_elect_his_no = #{safElectHisNo}

mapper.delete:
/* ElectHisMapper.deleteElectHis [ElectHis.xml] */
    delete from saf_elect_his
    where saf_elect_his_no = #{safElectHisNo}

/* ElectHisMapper.revDeleteElectHis [ElectHis.xml] */
    delete from saf_elect_his
    where saf_elect_his_no = #{safElectHisNo}

mapper.select:
/* ElectHisMapper.getCheckElectHis [ElectHis.xml] */
    select count(*) as cnt
        ,'user' as gubun
    from saf_elect_his
    where 1=1
    and user_id = #{userId}
    and saf_elect_titl_no = #{safElectTitlNo}

    and saf_elect_his_no not in (#{safElectHisNo})

/* ElectHisMapper.countElectHisNm [ElectHis.xml] */
    select count(*) as cnt from
        saf_elect_his
    where plant_cd=#{plantCd}
        and user_id=#{userId}
        and saf_elect_titl_no=#{safElectTitlNo}

/* ElectHisMapper.getHrElectTitlLcnsTotSize [ElectHis.xml] */ select count(*) from (SELECT LAW_MAN_NM, SABUN, lic_s_ymd, license_gubun, license_gubun_nm, license_nm, license_no, office_cd FROM OPENQUERY(ehr, 'SELECT LAW_MAN_NM, SABUN, lic_s_ymd, license_gubun, EHR_PSC.F_COM_GET_GRCODE_NAME(ENTER_CD, ''H20170'', LICENSE_GUBUN) as license_gubun_nm , license_no, license_nm, office_cd FROM EHR_PSC.THRM113 where enter_cd=''PSC'' ')) a inner join com_user b on a.sabun=b.user_id and b.use_yn='Y' and a.license_nm is not null inner join com_dept c on b.dept_cd=c.dept_cd and c.use_yn='Y' inner join dbo.LANG_CODE_MASTER(#{defaultParam.lang}, 'COM_PLANT_CD') d on d.code =c.plant_cd where 1=1
and c.plant_cd= #{plantCd}
and b.user_nm like '%'+#{userNm}+'%'
and license_nm like '%'+#{licenseNm}+'%'

/* ElectHisMapper.getElectStatus[ElectHis.xml] */ select eh.saf_elect_titl_no, et.elect_titl_nm ,sum(case when eh.plant_cd='1000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '1000' ,sum(case when eh.plant_cd='1500' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '1500' ,sum(case when eh.plant_cd='3000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '3000' ,sum(case when eh.plant_cd='4000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '4000' ,sum(case when eh.plant_cd='5000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '5000' ,sum(case when isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) elect_total from saf_elect_his eh inner join saf_elect_titl et on eh.saf_elect_titl_no = et.saf_elect_titl_no where eh.use_yn= 'Y'
and eh.saf_elect_titl_no = #{safElectTitlNo}
group by eh.saf_elect_titl_no, et.elect_titl_nm
union all
select '합계','합계' ,sum(case when eh.plant_cd='1000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '1000' ,sum(case when eh.plant_cd='1500' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '1500' ,sum(case when eh.plant_cd='3000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '3000' ,sum(case when eh.plant_cd='4000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '4000' ,sum(case when eh.plant_cd='5000' and isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) as '5000' ,sum(case when isnull(eh.elect_end_dt,'') = '' then 1 else 0 end) elect_total from saf_elect_his eh inner join saf_elect_titl et on eh.saf_elect_titl_no = et.saf_elect_titl_no where (eh.elect_end_dt is null or eh.elect_end_dt='')
